import React from "react";
import { UsersIcon } from "@heroicons/react/solid";
import { testimonials } from "../data";
import { Carousel } from 'react-responsive-carousel'
import "react-responsive-carousel/lib/styles/carousel.min.css";

const testimonialsImgs = testimonials.map((testimonial) =>
  <div>
      <img className="img-fluid" key={testimonial.image} src={testimonial.image} alt="Product"/>
  </div>
)

export default function Testimonials() {  
  return (
    <section id="testimonials">
      <div className="container pb-24 mx-auto">
        <div className="flex flex-wrap -m-4"></div>   
        <div className="text-center text-gray-800">
          <UsersIcon className="w-10 inline-block mb-4"/>
          <h1 className="sm:text-4xl text-3xl font-medium title-font mb-4">
              תגובות 
          </h1>          
        </div>
        </div>  
        <div class="carousel-wrapper"><Carousel>{testimonialsImgs}</Carousel></div> 
          
    </section>    
  );
}
