import React from "react";
import { skills } from "../data";
import { AcademicCapIcon } from "@heroicons/react/solid";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Ic} from "@fortawesome/react-fontawesome";
import { faGooglePay } from "@fortawesome/free-brands-svg-icons";

export default function Skills() {
  return (
    <section id="skills">   
    <div className="container py-24 mx-auto">
      <div className="flex flex-wrap -m-4"></div>   
        <div className="text-center text-gray-800">
          <AcademicCapIcon className="w-10 inline-block mb-4"/>
          <h1 className="sm:text-4xl text-3xl font-medium title-font mb-4">
            ידע &amp; כלים
          </h1>          
        </div>
        <div className="text-center max-w-sm max-h-sm rounded overflow-hidden shadow-lg">
          {skills.map((skill) => (
            <div key={skill.title} className="p-2">
                <div className="max-w-sm rounded overflow-hidden shadow-lg">
                  <FontAwesomeIcon icon={skill.skillIcon}/>
                  <div className="px-6 py-4">
                    <div className="font-bold text-xl mb-2">{skill.title}</div>
                    <p className="text-gray-700 text-base">
                    {skill.description}
                    </p>
                  </div>                  
                </div>
            </div>
          ))}
        </div>  
        </div>      
    </section>
  );
}
